import React, { useEffect, useState } from 'react';
import { MDBListGroup, MDBListGroupItem } from 'mdb-react-ui-kit';
import './services.css';

const servicesData = [
  {
    title: 'MANICURES',
    image: '/images/mani.jpeg',
    items: [
      { title: 'Basic Manicure', description: 'Includes the basic cleaning of nails with cuticles trimmed, nails shaping, light exfoliation, massage, and choice of polish' },
      { title: 'Spa Manicure' },
      { title: 'Gel Manicure' },
      { title: 'SNS Manicure' },
    ],
  },
  // Add more service categories here...
];

export default function App() {
  const [loadedServices, setLoadedServices] = useState({});

  useEffect(() => {
    const handleScroll = () => {
      const scrollY = window.scrollY;
      const elementsToFadeIn = document.querySelectorAll('.fade-in');

      elementsToFadeIn.forEach((element) => {
        const elementOffset = element.offsetTop;
        const windowHeight = window.innerHeight;
        const serviceId = element.getAttribute('data-service-id');

        if (scrollY > elementOffset - windowHeight / 2) {
          setLoadedServices((prevState) => ({
            ...prevState,
            [serviceId]: true,
          }));
        } else {
          setLoadedServices((prevState) => ({
            ...prevState,
            [serviceId]: false,
          }));
        }
      });
    };

    const debounce = (func, delay) => {
      let timeout;
      return function () {
        clearTimeout(timeout);
        timeout = setTimeout(() => {
          func.apply(this, arguments);
        }, delay);
      };
    };

    const debouncedHandleScroll = debounce(handleScroll, 100);

    window.addEventListener('scroll', debouncedHandleScroll);
    return () => {
      window.removeEventListener('scroll', debouncedHandleScroll);
    };
  }, []);

  return (
    <div className='all-services'>
      {servicesData.map((service, index) => (
        <div key={index} className={`service-item fade-in ${loadedServices[index] ? 'loaded' : ''}`} data-service-id={index}>
          <div>
            <img src={service.image} alt={service.title} />
          </div>
          <div>
            <h2>{service.title}</h2>
            <MDBListGroup style={{ minWidth: '22rem' }} light className='mb-3'>
              {service.items.map((item, itemIndex) => (
                <MDBListGroupItem key={itemIndex}>
                  <h5 className='fw-bold'>{item.title}</h5>
                  {item.description && <p className='text-muted mb-0'>{item.description}</p>}
                </MDBListGroupItem>
              ))}
            </MDBListGroup>
          </div>
        </div>
      ))}
    </div>
  );
}
