import React, { useState, useEffect } from 'react';
import './header.css';
import {
  MDBBtn,
  MDBNavbarLink,
} from 'mdb-react-ui-kit';

export default function Header() {
  const [fadeIn, setFadeIn] = useState(false);

  useEffect(() => {
    // Trigger the fade-in effect after the component is mounted
    setFadeIn(true);
  }, []);

  return (
    <header>
      <div
        id='headersec'
        className={`p-5 text-center bg-image ${fadeIn ? 'fade-in' : ''}`}
      >
        <div className={`header-des ${fadeIn ? 'fade-in' : ''}`}>
          <div className='d-flex justify-content-center align-items-center h-100'>
            <div className='text-black'>
              <h1 className='mb-3'>Welcome</h1>
              <h3>We offer the highest level of services to our clients</h3>
              <h5 className='mb-4'>Browse our services and make an appointment today</h5>
              <div className='header-buttons'>
                <MDBNavbarLink>
                  <MDBBtn
                    outline
                    rounded
                    className='mx-2 custom-hover'
                    color='dark'
                    href='services'
                  >
                    Services
                  </MDBBtn>
                  <MDBBtn
                    outline
                    rounded
                    className='mx-2 custom-hover'
                    color='dark'
                    href='contacts'
                  >
                    Contact
                  </MDBBtn>
                </MDBNavbarLink>
              </div>
            </div>
          </div>
        </div>
      </div>
    </header>
  );
}
