import React, { useState, useEffect } from 'react';
import { Link } from 'react-router-dom';
import './navbar.css';
import {
  MDBContainer,
  MDBNavbar,
  MDBNavbarToggler,
  MDBNavbarNav,
  MDBNavbarItem,
  MDBNavbarLink,
  MDBCollapse,
  MDBIcon,
  MDBBtn
} from 'mdb-react-ui-kit';

export default function App() {
  const [showNav, setShowNav] = useState(false);
  const [smallIcons, setSmallIcons] = useState(false);

  const toggleVisibility = () => {
    setShowNav(!showNav);
    setSmallIcons(!smallIcons);
  };

  useEffect(() => {
    // Check the window width and set smallIcons accordingly
    const handleResize = () => {
      setSmallIcons(window.innerWidth <= 375);
    };

    handleResize();

    window.addEventListener('resize', handleResize);

    return () => {
      window.removeEventListener('resize', handleResize);
    };
  }, []);

  return (
    <MDBNavbar expand='lg' light bgColor='light' className="navbar">
      <MDBContainer fluid>
        <MDBNavbarToggler
          type='button'
          aria-expanded='false'
          aria-label='Toggle navigation'
          onClick={toggleVisibility}
        >
          <MDBIcon icon='bars' fas />
        </MDBNavbarToggler>

        <MDBCollapse navbar show={showNav}>
          <MDBNavbarNav>
            {/* Your menu items */}
          </MDBNavbarNav>
        </MDBCollapse>

        <div>
          <Link to="/" className='business-name'>
            {smallIcons ? '' : 'Moon Nails'}
          </Link>
        </div>

        {showNav && (
          <div className='social-icons'>
            <MDBBtn outline color="dark" floating className='m-1' href='https://www.facebook.com/people/Concord-Nail-Tek/100095126682743/' role='button' target="_blank">
              <MDBIcon fab icon='facebook' size={smallIcons ? 'lg' : '2x'} style={{ color: 'black' }} />
            </MDBBtn>

            <MDBBtn outline color="dark" floating className='m-1' href='https://www.instagram.com/moonnails8639/' role='button' target="_blank">
              <MDBIcon fab icon='instagram' size={smallIcons ? 'lg' : '2x'} style={{ color: 'black' }} />
            </MDBBtn>
          </div>
        )}
      </MDBContainer>
    </MDBNavbar>
  );
}
